import React, { Fragment, useState } from "react";

const EditBook = ({ book }) => {
  const [bookId, setBookID] = useState(book.book_id);
  const [bookTitle, setBookTitle] = useState(book.title);
  const [yearPublished, setYearPublished] = useState(book.year_published);
  const [pages, setPages] = useState(book.pages);
  const [price, setPrice] = useState(book.price);

  //edit Book

  const updateBook= async e => {
    e.preventDefault();
    try {
      const body = {  
        book_id : bookId,
        title: bookTitle,
        year_published: yearPublished,
        pages,
        price };
      const response = await fetch(
        `http://localhost:3000/books/${book.book_id}`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body)
        }
      );

      // Refresh the page to reflect the updated data
      window.location.reload();
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <Fragment>
      <button
        type="button"
        class="btn btn-warning"
        data-toggle="modal"
        data-target={`#id${book.book_id}`}
      >
        Edit
      </button>

      <div
        className="modal"
        id={`id${book.book_id}`}
        onClick={() => {
          setBookID(book.book_id);
          setBookTitle(book.title);
          setYearPublished(book.year_published);
          setPages(book.pages);
          setPrice(book.price);
        }}
      >
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h4 className="modal-title">Edit Books</h4>
              <button
                type="button"
                className="close"
                data-dismiss="modal"
                onClick={() => {
                  setBookID(book.book_id);
                  setBookTitle(book.title);
                  setYearPublished(book.year_published);
                  setPages(book.pages);
                  setPrice(book.price);
                }}
              >
                &times;
              </button>
            </div>

            <div className="modal-body">
              <div className="form-group">
                <label htmlFor="book_id">Book ID</label>
                <input
                  type="text"
                  className="form-control"
                  id="book_id"
                  value={bookId}
                  onChange={(e) => setBookID(e.target.value)}
                />
              </div>
              <div className="form-group">
                <label htmlFor="title">Book Title</label>
                <input
                  type="text"
                  className="form-control"
                  id="title"
                  value={bookTitle}
                  onChange={(e) => setBookTitle(e.target.value)}
                />
              </div>
              <div className="form-group">
                <label htmlFor="year_published">Publication Year</label>
                <input
                  type="text"
                  className="form-control"
                  id="year_published"
                  value={yearPublished}
                  onChange={(e) => setYearPublished(e.target.value)}
                />
              </div>

              <div className="form-group">
                <label htmlFor="pages">Pages</label>
                <input
                  type="text"
                  className="form-control"
                  id="pages"
                  value={pages}
                  onChange={(e) => setPages(e.target.value)}
                />
              </div>
              <div className="form-group">
                <label htmlFor="price">Book Price</label>
                <input
                  type="text"
                  className="form-control"
                  id="price"
                  value={price}
                  onChange={(e) => setPrice(e.target.value)}
                />
              </div>
            </div>

            <div className="modal-footer">
              <button
                type="button"
                class="btn btn-warning"
                data-dismiss="modal"
                onClick={(e) => updateBook(e)}
              >
                Edit
              </button>
              <button
                type="button"
                className="btn btn-danger"
                data-dismiss="modal"
                onClick={() => {
                  setBookID(book.book_id);
                  setBookTitle(book.title);
                  setYearPublished(book.year_published);
                  setPages(book.pages);
                  setPrice(book.price);
                }}
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default EditBook;
