import React, { Fragment, useState } from "react";

const EditBook = ({ book }) => {
  const [bookId, setBookID] = useState(book.books_id);
  const [bookTitle, setBookTitle] = useState(book.title);
  const [yearPublished, setYearPublished] = useState(book.year_published);
  const [pages, setPages] = useState(book.pages);
  const [price, setPrice] = useState(book.price);

  //edit description function

  const updateDescription = async e => {
    e.preventDefault();
    try {
      const body = {  
        books_id : bookId,
        title: bookTitle,
        year_published: yearPublished,
        pages,
        price };
      const response = await fetch(
        `http://localhost:3000/books/${books.books_id}`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body)
        }
      );

      window.location = "/";
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <Fragment>
      <button
        type="button"
        class="btn btn-warning"
        data-toggle="modal"
        data-target={`#id${books.books_id}`}
      >
        Edit
      </button>

      {/* 
        id = id10
      */}
      <div
        className="modal"
        id={`id${books.books_id}`}
        onClick={() => setDescription(books.description)}
      >
        <div class="modal-dialog">
          <div class="modal-content">
            <div class="modal-header">
              <h4 class="modal-title">Edit Books</h4>
              <button
                type="button"
                class="close"
                data-dismiss="modal"
                onClick={() => setDescription(books.description)}
              >
                &times;
              </button>
            </div>

            <div class="modal-body">
              <input
                type="text"
                className="form-control"
                value={description}
                onChange={e => setDescription(e.target.value)}
              />
            </div>

            <div class="modal-footer">
              <button
                type="button"
                class="btn btn-warning"
                data-dismiss="modal"
                onClick={e => updateDescription(e)}
              >
                Edit
              </button>
              <button
                type="button"
                class="btn btn-danger"
                data-dismiss="modal"
                onClick={() => setDescription(books.description)}
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default EditBooks;
